INSERT INTO `play_templates` VALUES
(1,1,'==В ролях:==\r\n\'\'\'Флория Тоска\'\'\', знаменитая певица — Ольга Черемных\r\n\'\'\'Марио Каварадосси\'\'\', художник — Дмитрий Каляка\r\n\'\'\'Скарпиа\'\'\', шеф полиции — Владимир Целебровский\r\n\'\'\'Чезаре Анджелотти\'\'\', политический узник — Андрей Удалов\r\n\'\'\'Ризничий\'\'\' — Иван Красавцев\r\n\'\'\'Сполетта\'\'\', полицейский доносчик — Юрий Васильев\r\n\'\'\'Шарроне\'\'\', жандарм — Семён Фатыхов\r\n\'\'\'Тюремщик\'\'\' — Кирилл Жуков\r\n\'\'\'Мальчик\'\'\' (голос за сценой) — Елизавета Майданова\r\n\'\'\'Роберти\'\'\', палач — Кирилл Костромин\r\n\r\n[[photo403958_457284852|400x600px| ]]\r\n\r\n\'\'\'Дирижёр\'\'\' — Павел Бубельников\r\n\r\n\'\'\'[http://www.zazerkal.spb.ru/tickets/{CODE}.htm|КУПИТЬ БИЛЕТ]\'\'\'','2025-09-07 02:43:46','2025-09-07 12:34:35'),
